import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const Section = ({ title, description, buttons, delay, onClickHandlers }) => {
  const [isHovered, setIsHovered] = React.useState(false);

  const cardVariants = {
    hidden: { opacity: 0, y: 50, rotateX: 45 },
    visible: { 
      opacity: 1, 
      y: 0, 
      rotateX: 0,
      transition: {
        duration: 0.8,
        delay,
        ease: "easeOut",
        staggerChildren: 0.2
      }
    }
  };

  const contentVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: { opacity: 1, x: 0 }
  };

  const buttonContainerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.1
      }
    }
  };

  const buttonVariants = {
    hidden: { scale: 0, opacity: 0 },
    visible: { 
      scale: 1, 
      opacity: 1,
      transition: { type: "spring", stiffness: 200, damping: 10 }
    }
  };

  const shimmerEffect = {
    hidden: { backgroundPosition: "200% 0" },
    visible: { 
      backgroundPosition: "-200% 0",
      transition: {
        repeat: Infinity,
        duration: 3,
        ease: "linear"
      }
    }
  };

  return (
    <motion.div
      variants={cardVariants}
      initial="hidden"
      animate="visible"
      className={`bg-gradient-to-br from-gray-100 to-gray-200 p-6 rounded-lg shadow-lg 
                  hover:shadow-xl transition-all duration-300 relative overflow-hidden
                  ${isHovered ? 'translate-y-[-5px]' : ''}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Animated background gradient */}
      <motion.div
        variants={shimmerEffect}
        initial="hidden"
        animate="visible"
        className="absolute inset-0 bg-gradient-to-r from-transparent via-red-100/10 to-transparent"
        style={{ backgroundSize: "200% 100%" }}
      />

      <motion.div className="relative z-10">
        <motion.h2 
          variants={contentVariants}
          className="text-2xl font-bold text-red-600 mb-3"
          animate={{
            scale: isHovered ? 1.05 : 1,
            color: isHovered ? '#DC2626' : '#EF4444',
          }}
          transition={{ duration: 0.3 }}
        >
          {title}
        </motion.h2>

        <motion.p 
          variants={contentVariants}
          className="text-gray-700 mb-4"
        >
          {description}
        </motion.p>

        <motion.div 
          className="flex flex-wrap gap-3"
          variants={buttonContainerVariants}
        >
          {buttons.map((button, index) => (
            <motion.button
              key={index}
              variants={buttonVariants}
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 5px 15px rgba(220, 38, 38, 0.3)",
              }}
              whileTap={{ scale: 0.95 }}
              onClick={onClickHandlers[index]}
              className={`bg-red-600 text-white px-4 py-2 rounded-md relative
                        overflow-hidden transition-colors duration-300
                        hover:bg-red-700 focus:outline-none focus:ring-2 
                        focus:ring-red-500 focus:ring-opacity-50`}
            >
              <motion.span
                initial={{ y: 0 }}
                animate={isHovered ? { y: [-1, 1] } : { y: 0 }}
                transition={{
                  duration: 0.5,
                  repeat: Infinity,
                  repeatType: "reverse"
                }}
                className="block"
              >
                {button}
              </motion.span>
            </motion.button>
          ))}
        </motion.div>
      </motion.div>
    </motion.div>
  );
};

const RecruitmentSections = () => {
  const navigate = useNavigate();

  return (
    <div className="max-w-6xl mx-auto p-6">
      <motion.div 
        className="grid grid-cols-1 md:grid-cols-2 gap-6"
        initial="hidden"
        animate="visible"
        variants={{
          visible: {
            transition: {
              staggerChildren: 0.2
            }
          }
        }}
      >
        <Section
          title="Recruitment Solutions"
          description="Thousands of jobs across India. Hundreds of clients across India."
          buttons={['Job Seekers', 'Employers', 'Vendors']}
          delay={0.1}
          onClickHandlers={[
            () => window.open('https://kaarlo.zohorecruit.in/candidateportal', '_blank'),
            () => window.open('https://kaarlo.zohorecruit.in/clientportal', '_blank'),
            () => window.open('https://kaarlo.zohorecruit.in/vendorportal', '_blank'),
          ]}
        />

        <Section
          title="Training Solutions"
          description="Expert in Corporate Training, Campus Training, HR Training"
          buttons={['For Companies', 'Institutions', 'Courses']}
          delay={0.2}
          onClickHandlers={[
            () => navigate('/training/companies'),
            () => navigate('/training/institutions'),
            () => navigate('/training/courses'),
          ]}
        />

        <Section
          title="Internship"
          description="Join as a student and leave as an employee"
          buttons={['Inquire Now']}
          delay={0.3}
          onClickHandlers={[
            () => navigate('/internship/inquire'),
          ]}
        />

        <Section
          title="Organizational Development Solutions"
          description="Engage your employees and increase your profits"
          buttons={['Inquire Now']}
          delay={0.4}
          onClickHandlers={[
            () => navigate('/od-solutions/inquire'),
          ]}
        />
      </motion.div>
    </div>
  );
};

export default RecruitmentSections;
