import React from 'react'; 
import { motion } from 'framer-motion';
import { MapPin, Phone, Mail, Linkedin, Facebook,Twitter, Calendar, ChevronRight, ArrowRight } from 'lucide-react'; 
import placeholderImage from './pages/placeholder.jpg';


{/* Hero Section with gradient background and modern design */}


const HeroSection = () => {
  return (
    <div className="min-h-screen flex flex-col">
      <section className="relative bg-gradient-to-br from-red-600 via-red-500 to-red-700 py-32 overflow-hidden">
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 0.1 }}
          transition={{ duration: 1.5 }}
          style={{ backgroundImage: `url(${placeholderImage})` }}
          className="absolute inset-0  mix-blend-overlay"
        />
        <div className="container mx-auto px-4 relative">
          <div className="text-center">
            <motion.h1
              initial={{ y: -50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8 }}
              className="text-5xl md:text-6xl font-bold mb-6 text-white leading-tight"
            >
              Your Trusted
              <motion.span
                initial={{ x: -30, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                transition={{ delay: 0.3, duration: 0.8 }}
                className="block text-red-200"
              >
                Staffing Partner
              </motion.span>
            </motion.h1>
            <motion.p
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.6, duration: 0.8 }}
              className="text-xl md:text-2xl text-red-100 mb-12 max-w-2xl mx-auto"
            >
              Connecting exceptional talent with visionary businesses for tomorrow's success
            </motion.p>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.9, duration: 0.8 }}
              className="bg-white text-red-600 px-8 py-4 rounded-full hover:bg-red-50 shadow-lg font-semibold transition-colors duration-300"
            >
              Get Started
            </motion.button>
          </div>
        </div>
      </section>
    </div>
  );
};


{/* Services Section with cards */}
const services = [
  {
    title: 'Permanent Staffing',
    description: 'Find your perfect long-term team members with our proven recruitment process.',
    icon: 'üë•',
  },
  {
    title: 'Contract Hiring',
    description: 'Flexible staffing solutions for your project-based requirements.',
    icon: 'üìã',
  },
  {
    title: 'Executive Search',
    description: 'Premium recruitment service for senior leadership positions.',
    icon: 'üéØ',
  },
];

const WhatWeDo = () => { return (
<section className="py-24 bg-gray-50">
  <div className="container mx-auto px-4">
    <h2 className="text-4xl font-bold text-center mb-4">What We Do</h2>
    <p className="text-gray-600 text-center mb-16 max-w-2xl mx-auto">
      Comprehensive staffing solutions tailored to your unique business needs
    </p>
    <div className="grid md:grid-cols-3 gap-8">
      {services.map((service) => (
      <div
        key="{service.title}"
        className="p-8 bg-white rounded-2xl hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2 border border-gray-100"
      >
        <div className="text-4xl mb-6">{service.icon}</div>
        <h3 className="text-2xl font-semibold mb-4">{service.title}</h3>
        <p className="text-gray-600 mb-6">{service.description}</p>
        <a
          href="#"
          className="text-blue-600 font-medium flex items-center group"
        >
          Learn More
          <span
            className="w-4 h-4 ml-2 transform group-hover:translate-x-2 transition-transform"
            role="img"
            aria-label="arrow"
          >
            ‚û°Ô∏è
          </span>
        </a>
      </div>
      ))}
    </div>
  </div>
</section>
); }; 

const statistics = [
  { number: '500+', label: 'Successful Placements' },
  { number: '98%', label: 'Client Satisfaction' },
  { number: '50+', label: 'Industry Partners' },
  { number: '10+', label: 'Years Experience' },
];

const StatsSection = () => { return (
<section className="py-16 bg-blue-600 text-white">
  <div className="container mx-auto px-4">
    <div className="grid md:grid-cols-4 gap-8 text-center">
      {statistics.map((stat) => (
      <div key="{stat.label}" className="p-6">
        <div className="text-4xl font-bold mb-2">{stat.number}</div>
        <div className="text-blue-100">{stat.label}</div>
      </div>
      ))}
    </div>
  </div>
</section>
); }; {/* Why Choose Us with modern cards */} const reasons = [ { title:
'Industry Expertise', icon: 'üéì' }, { title: 'Quick Turnaround', icon: '‚ö°' }, {
title: 'Quality Candidates', icon: 'üåü' }, { title: 'Ongoing Support', icon:
'ü§ù' }, ]; const WhyChooseUs = () => { return (
<section className="py-24">
  <div className="container mx-auto px-4">
    <h2 className="text-4xl font-bold text-center mb-16">Why Choose Us</h2>
    <div className="grid md:grid-cols-4 gap-8">
      {reasons.map((reason) => (
      <div
        key="{reason.title}"
        className="text-center p-6 rounded-xl hover:bg-blue-50 transition-colors duration-300"
      >
        <div className="text-4xl mb-4">{reason.icon}</div>
        <h3 className="text-xl font-semibold mb-3">{reason.title}</h3>
        <p className="text-gray-600">
          Delivering excellence in staffing solutions.
        </p>
      </div>
      ))}
    </div>
  </div>
</section>
); }; 

{/* Recent Works & Life at Kaarlo with modern design */} 

const successStories = ['Project 1', 'Project 2', 'Project 3']; 
const lifeAtKaarloImages = [1, 2, 3, 4]; 

const StoriesAndLife = () => { 
  return (
<section className="bg-gray-50 py-24">
  <div className="container mx-auto px-4">
    <div className="grid md:grid-cols-2 gap-16">
      {/* Recent Success Stories */}
      <div>
        <h2 className="text-3xl font-bold mb-8">Recent Success Stories</h2>
        <div className="space-y-6">
          {successStories.map((project) => (
          <div
            key="{project}"
            className="p-6 bg-white rounded-xl shadow-sm hover:shadow-md transition-shadow duration-300 border border-gray-100"
          >
            <h3 className="font-semibold text-xl mb-2">{project}</h3>
            <p className="text-gray-600">Successful placement case study.</p>
          </div>
          ))}
        </div>
      </div>

      {/* Life at Kaarlo */}
      <div>
        <h2 className="text-3xl font-bold mb-8">Life at Kaarlo</h2>
        <div className="grid grid-cols-2 gap-6">
          {lifeAtKaarloImages.map((img) => (
          <div
            key="{img}"
            className="aspect-square bg-gradient-to-br from-blue-100 to-blue-200 rounded-xl overflow-hidden transform hover:scale-105 transition-transform duration-300"
          >
            <img src={"../placeholder.jpg"} alt="Life at Kaarlo"
            className="w-full h-full object-cover" />
          </div>
          ))}
        </div>
      </div>
    </div>
  </div>
</section>
); };

const components = {
  StoriesAndLife,
  WhyChooseUs,
  StatsSection,
  WhatWeDo,
  HeroSection,
};

export default components;
